void PropertiesDialog::OnApplyUpdateUI (wxUpdateUIEvent &event)
{
    for( int i = 0; i < m_properties->GetPageCount(); ++i )
    {
        PropertyPageBase *page = dynamic_cast<PropertyPageBase *>( m_properties->GetPage( i ) );
        if( page->IsModified() )
            event.Enable( true );
    }
}
